mapper.select:
select count(*) from member

	where id like #{keyword}
	or name like #{keyword}
	or email1 like #{keyword}
	or email2 like #{keyword}

select count(*) from business

	where id like #{keyword}
	or name like #{keyword}
	or email1 like #{keyword}
	or email2 like #{keyword}

select * from member

	where id like #{keyword}
	or name like #{keyword}
	or email1 like #{keyword}
	or email2 like #{keyword}

select * from business

	where id like #{keyword}
	or bcode like #{keyword}
	or id like #{keyword}
	or type like #{keyword}
	or addr1 like #{keyword}
	or addr2 like #{keyword}

mapper.update:
update member 
set password=#{password}, name=#{name}, phone1=#{phone1}, phone2=#{phone2}, phone3=#{phone3}, email1=#{email1}, email2=#{email2} 
where id=#{id}
