mapper.select:
    select count(*) as cnt from notice






	                where subject like #{keyword}


	                where category like #{keyword}

select * from notice
where noti = 'n'



            and subject like #{keyword}


            and category like #{keyword}



order by nnum desc

select * from notice
where noti = 'y'



            and subject like #{keyword}


            and category like #{keyword}



order by nnum desc

    select count(*) as cnt from description






	                where dsubject like #{keyword}


	                where id like #{keyword}

	    SELECT dnum, id, dsubject, password, ddate, ref, re_step, re_level, dcontent
    		FROM (
       		SELECT rownum AS rank, dnum, id, dsubject, password,ddate, ref, re_step, re_level, dcontent
       			FROM (
            	SELECT dnum, id, dsubject, password,ddate, ref, re_step, re_level, dcontent
            		FROM description



					                where dsubject like #{keyword}


					                where id like #{keyword}



			    ORDER BY ref DESC, re_step ASC
			    )
		    )
    	ORDER BY ref DESC, re_step ASC

select password
from description
where dnum = #{dnum}

mapper.update:
	update description
    set dsubject = #{dsubject}, dcontent = #{dcontent}
    where dnum = #{dnum}

update description
set re_step = re_step + 1
where ref = #{ref} and re_step > #{re_step}
